mapper.select:
select s_name, s_no
from stadium
where p_num = #{p_num}

select r.m_id, m.m_name, p.p_name, r.r_total_pay, r.refund, to_char(r.r_recall_time, 'yyyy/MM/dd') r_recall_time, r.r_no, to_char(m.m_regdate, 'yyyy/MM/dd') m_regdate from rental r, member m, place p
r.m_id = m.m_id and p.p_num = (select p_num from stadium where s_no = r.s_no) and p.m_id = 'esub17@naver.com' and r.r_pay_status in (-1, 2)
and to_char(r.r_recall_time, 'yyyy/MM') = to_char(sysdate, 'yyyy/MM')
and to_char(r.r_recall_time, 'yyyy') = #{p_open}
and to_char(r.r_recall_time, 'yyyy/MM') = #{p_open}
order by r_recall_time desc
